package de.dfki.iui.basys.runtime.services.impl;

import java.util.HashMap;
import java.util.Map;

import javax.ws.rs.core.Response;

import de.dfki.iui.basys.model.runtime.component.ComponentConfiguration;
import de.dfki.iui.basys.runtime.component.service.EmfServiceComponent;
import de.dfki.iui.basys.runtime.services.WorkerGuidanceManager;

public class WorkerGuidanceManagerImpl extends EmfServiceComponent implements WorkerGuidanceManager {

	public WorkerGuidanceManagerImpl(ComponentConfiguration config) {
		super(config);
	}

	private String createBody(String id, int order, int serial, int matNr, int errorCode) {

		// job mockup
		// constants 
		int BASYS_MAT_NR = 12345;
		String ERROR_DEFAULT_MESSAGE = "Fehler nicht bekannt!";
		
		// images
		String EINLEGEN_IMAGE = "data:image/png;base64,";
		String FUEGEN_IMAGE = "data:image/png;base64,";
		
		// error code/message map
		Map<Integer, String> errorCodeMessages = new HashMap<>();
		errorCodeMessages.put(0, "Ablauf funktioniert ohne Fehler/ Einschränkungen");
		errorCodeMessages.put(1, "Lichtgitter durchbrochen");
		errorCodeMessages.put(2, "Magazin A leer");
		errorCodeMessages.put(3, "Magazin A falsch bestückt/ \r\n" + "Deckel verklemmt");
		errorCodeMessages.put(4, "Magazin B leer");
		errorCodeMessages.put(5, "Magazin B falsch bestückt/ \r\n" + "Deckel verklemmt");
		errorCodeMessages.put(6, "Baugruppe nicht bestückt");
		errorCodeMessages.put(7, "Baugruppe bereits mit Deckel \r\n" + "bestückt");
		errorCodeMessages.put(8, "Nicht erlaubte Achsposition");
		errorCodeMessages.put(9, "Greifen fehlgeschlagen");
		errorCodeMessages.put(10, "Achsen blockiert");
		errorCodeMessages.put(11, "Notaus betätigt");
		errorCodeMessages.put(13, "Deckel während des Greifens\r\n" + "verloren");
		// --------------------------
		
		String heading = "";
		String imageBase64 = "";
		boolean showLoadingIndicator = false;
		if (matNr == BASYS_MAT_NR) {
			switch (id) {
			case "einlegen":
				heading = "Bitte Verteilergehäuse in Festo-Station einlegen.";
				imageBase64 = EINLEGEN_IMAGE;
				break;
			case "fuegen":
				heading = "Deckel wird gefügt.";
				imageBase64 = FUEGEN_IMAGE;
				showLoadingIndicator = true;
				break;
			case "error":
				heading = errorCodeMessages.getOrDefault(errorCode, ERROR_DEFAULT_MESSAGE);
				break;
			default:
				break;
			}
		} else {
			switch (id) {
			case "einlegen":
				heading = "Bitte Verteilergehäuse in Festo-Station einlegen.";
				imageBase64 = EINLEGEN_IMAGE;
				break;
			case "fuegen":
				heading = "Deckel wird gefügt.";
				imageBase64 = FUEGEN_IMAGE;
				showLoadingIndicator = true;
				break;
			case "error":
				heading = errorCodeMessages.getOrDefault(errorCode, ERROR_DEFAULT_MESSAGE);
				break;
			default:
				break;
			}
		}
		// ------------------------------------

		String body = createHTMLforBody(heading, imageBase64, showLoadingIndicator);

		return body;
	}

	private String createHTMLforBody(String heading, String imageBase64, boolean showLoadingIndicator) {
		String body = "<!DOCTYPE html>\r\n" + 
				"<html lang=\"en\">\r\n" + 
				"<head>\r\n" + 
				"    <meta charset=\"UTF-8\">\r\n" + 
				"    <title>Title</title>\r\n" + 
				"\r\n" + 
				"    <style>\r\n" + 
				"\r\n" + 
				"        html {\r\n" + 
				"            font-family: Arial,sans-serif;\r\n" + 
				"            height: 100%;\r\n" + 
				"            font-size: 2em;\r\n" + 
				"        }\r\n" + 
				"\r\n" + 
				"        body {\r\n" + 
				"            height: 100%;\r\n" + 
				"            margin: 0;\r\n" + 
				"        }\r\n" + 
				"\r\n" + 
				"        .loader {\r\n" + 
				"            border: 8px solid #f3f3f3; /* Light grey */\r\n" + 
				"            border-top: 8px solid #3498db; /* Blue */\r\n" + 
				"            border-radius: 50%;\r\n" + 
				"            width: 20px;\r\n" + 
				"            height: 20px;\r\n" + 
				"            animation: spin 2s linear infinite;\r\n" + 
				"            text-align: center;\r\n" + 
				"            margin: 0 auto;\r\n" + 
				"            position: absolute;\r\n" + 
				"            top: 0.4em;\r\n" + 
				"            right: 5%;\r\n" + 
				"        }\r\n" + 
				"\r\n" + 
				"        @keyframes spin {\r\n" + 
				"            0% { transform: rotate(0deg); }\r\n" + 
				"            100% { transform: rotate(360deg); }\r\n" + 
				"        }\r\n" + 
				"\r\n" + 
				"        #container {\r\n" + 
				"            width: 100%;\r\n" + 
				"            height: 100%;\r\n" + 
				"            position: relative;\r\n" + 
				"        }\r\n" + 
				"\r\n" + 
				"        #text-wrapper {\r\n" + 
				"            text-align: center;\r\n" + 
				"            margin-top: 0.4em;\r\n" + 
				"            position: absolute;\r\n" + 
				"            width: 100%;\r\n" + 
				"        }\r\n" + 
				"\r\n" + 
				"        #text-wrapper h2 {\r\n" + 
				"            margin-top: 0;\r\n" + 
				"        }\r\n" + 
				"\r\n" + 
				"        #content-logo {\r\n" + 
				"            width: 30%;\r\n" + 
				"            position: absolute;\r\n" + 
				"            margin: 0;\r\n" + 
				"            left: 50%;\r\n" + 
				"            top: 50%;\r\n" + 
				"            margin-right: -50%;\r\n" + 
				"            transform: translate(-50%, -50%)\r\n" + 
				"        }\r\n" + 
				"\r\n" + 
				"        #basys-logo {\r\n" + 
				"            width: 30%;\r\n" + 
				"            position: absolute;\r\n" + 
				"            right: 5px;\r\n" + 
				"            bottom: 2px;\r\n" + 
				"        }\r\n" + 
				"\r\n" + 
				"        h1 {\r\n" + 
				"            background-color: green;\r\n" + 
				"            color: white;\r\n" + 
				"        }\r\n" + 
				"\r\n" + 
				"        p {\r\n" + 
				"            text-align: center;\r\n" + 
				"            color: green;\r\n" + 
				"        }\r\n" + 
				"\r\n" + 
				"    </style>\r\n" + 
				"\r\n" + 
				"</head>\r\n" + 
				"<body>\r\n" + 
				"    <div id=\"container\">\r\n" + 
				"\r\n" + 
				"        <div id=\"text-wrapper\">\r\n" + 
				"            <h2>" + heading + "</h2>";
		
				if (showLoadingIndicator) {
					body += "<div class=\"loader\"></div>\r\n";
				}
		
				body += "        </div>\r\n" + 
				"\r\n"; 
				if (!imageBase64.equals("")) {
					body += "        <img id=\"content-logo\" src=\"" + imageBase64 + "\">\r\n";
				} 
				body += "\r\n" + 
				"        <img id=\"basys-logo\"\r\n" + 
				"             src=\"data:image/png;base64,\">\r\n" + 
				"    </div>\r\n" + 
				"</body>\r\n" + 
				"</html>";
				
				return body;
	}

	@Override
	public Response presentWorkerGuidance(String id, int order, int serial, int matNr, int errorCode) {
		String html = "" + "<html>" 
	+ "<head></head>" + "<body>" + createBody(id, order, serial, matNr, errorCode)
				+ "</body>" + "</html>";

		Response response = Response.ok(html).build();
		return response;
	}

}
